
var Q = require('q');

var attachToPack = function (pack, obj) {
    pack.res.content = JSON.stringify(obj);
    pack.res.contentType = 'text/json';
    return pack;
};

//output: {'returnCode':, 'msg':}
var getRCObj = function (_rc) {
    return { 'returnCode': _rc.rc, 'msg': _rc.msg };
};

var expectedError = function (ret) {
    var err = new Error();
    err.expected = true;
    err.hikerJoy_ret = ret;
    return err;
};

var getResolvedPromise = function () {
    var defer = new Q.defer();
    defer.resolve();
    return defer.promise;
};

exports.attachToPack = attachToPack;
exports.getRCObj = getRCObj;
exports.expectedError = expectedError;
exports.getResolvedPromise = getResolvedPromise;
