
var constants = require('./constants.js');

var createPlainOrg = function(alias, short, full) {
    return {
        'statusId': constants.orgStatus.active,
        'alias': alias,
        'shortName': short,
        'fullName': full,
        'typeCode': 'EDU',
        'bannerUrl': '/content/image/banner_default.jpg',
        'logoUrl': '/content/image/orgLogo_default.jpg',
        'createdOn': new Date()
    };
};

exports.createPlainOrg = createPlainOrg;